import os
import subprocess
from PIL import Image, ImageOps
import base64
import io
import sys

# 이미지 변환 함수: 특정 형식의 이미지를 JPG로 변환
def convert_to_jpg(file_path, output_file_path):
    subprocess.run(['magick', file_path, '-background', 'white', '-flatten', output_file_path])

# ImageMagick를 사용해야 하는지 결정하는 함수
def should_use_imagemagick(file_path):
    _, ext = os.path.splitext(file_path)
    ext = ext.lower()
    return ext in ['.avif', '.heif', '.heic']

# 워터마크 이미지의 중앙 좌표를 계산하는 함수
def calculate_watermark_position(base_image, overlay_image):
    base_width, base_height = base_image.size
    overlay_width, overlay_height = overlay_image.size

    # 워터마크를 이미지의 중앙에 위치시키는 좌표 계산
    position_x = (base_width - overlay_width) // 2
    position_y = (base_height - overlay_height) // 2

    return position_x, position_y

# 폴더 경로 설정
home_path = os.path.expanduser("~")
source_folder = os.path.join(home_path, 'Desktop', 'image')
export_folder = os.path.join(home_path, 'Desktop', 'image_export')

created_folders = []

# 폴더 생성 확인
if not os.path.exists(source_folder):
    os.makedirs(source_folder)
    created_folders.append(source_folder)

if not os.path.exists(export_folder):
    os.makedirs(export_folder)
    created_folders.append(export_folder)

if created_folders:
    for folder in created_folders:
        print(f"'{os.path.basename(folder)}' 폴더가 생성되었습니다. 경로: {folder}")
    input("종료하려면 Enter 키를 눌러주세요...")
    sys.exit()

if len(os.listdir(source_folder)) == 0:
    print("image 폴더에 저장된 이미지가 없습니다.")
    input("종료하려면 Enter 키를 눌러주세요...")
    sys.exit()

total_files = sum([len(files) for r, d, files in os.walk(source_folder)])
processed_files = 0

# 워터마크 이미지 로드 및 크기 조정
base64_overlay_image = ""
overlay_image_data = base64.b64decode(base64_overlay_image)
overlay_image = Image.open(io.BytesIO(overlay_image_data))
overlay_image = overlay_image.rotate(0, expand=True)
overlay_image = overlay_image.resize((276, 100))

# 워터마크 삽입 여부 확인
insert_watermark = input("워터마크를 삽입하시겠습니까? (y/n, 기본값: n): ").lower() or 'n'
while insert_watermark not in ['y', 'n']:
    insert_watermark = input("잘못된 입력입니다. 워터마크를 삽입하시겠습니까? (y/n, 기본값: n): ").lower() or 'n'

# 이미지 크기 설정
sizes = [(1000, 1000), (860, 860)]

for dir_path, dirs, files in os.walk(source_folder):
    relative_path = os.path.relpath(dir_path, source_folder)
    file_counter = 1

    for file_name in files:
        try:
            file_path = os.path.join(dir_path, file_name)

            if should_use_imagemagick(file_path):
                output_file_path = os.path.splitext(file_path)[0] + '.jpg'
                convert_to_jpg(file_path, output_file_path)
                os.remove(file_path)
                file_path = output_file_path

            img = Image.open(file_path).convert("RGBA")

            for size in sizes:
                destination_folder = os.path.join(export_folder, relative_path, str(size[0]))
                if not os.path.exists(destination_folder):
                    os.makedirs(destination_folder)

                output_file_path = os.path.join(destination_folder, f"{file_counter}.jpg")
                img_resized = ImageOps.fit(img, size, Image.LANCZOS, centering=(0.5, 0.5))
                background = Image.new("RGB", size, (255, 255, 255))
                background.paste(img_resized, mask=img_resized.split()[3])

                if insert_watermark == 'y':
                    watermark_position = calculate_watermark_position(background, overlay_image)
                    background.paste(overlay_image, watermark_position, mask=overlay_image)

                background.save(output_file_path, quality=100)

            file_counter += 1
            processed_files += 1
            print(f"진행 중인 파일: {processed_files}/{total_files}...")

        except Exception as e:
            print(f"{file_name} 이미지 처리 중 오류 발생. 오류: {str(e)}")

print("이미지 처리가 완료되었습니다.")
input("종료하려면 Enter 키를 눌러주세요...")
